import React, { useState, useRef } from "react";
import { useHistory } from "react-router-dom";
import { ChatEngine } from "react-chat-engine";
import { auth } from "../firebase";

import { useAuth } from "../contexts/AuthContext";
import { useEffect } from "react";
import axios from "axios";
const Chats = () => {
  const history = useHistory();
  const { user } = useAuth();
  const [loading, setLoading] = useState(true);

  const handleLogout = async () => {
    await auth.signOut();
    history.push("/");
  };

  const getFile = async (url) => {
    const response = await fetch(url);
    const data = await response.blob();

    return new File([data], "userPhoto.jpg", { type: "image/jpeg" });
  };
  useEffect(() => {
    if (!user) {
      history.push("/");

      return;
    }
    axios
      .get("https://api.chatengine.io/users/me/", {
        headers: {
          "project-id": "",
          "user-name": user.emai,
          "user-secret": user.uid,
        },
      })
      .then(() => {
        setLoading(false);
      });
  }, [user, history]).catch(() => {
    let formdata = new FormData();
    formdata.append("email", user.email);
    formdata.append("username", user.email);
    formdata.append("secret", user.uid);

    getFile(user.photoUrl).then((avatar) => {
      formdata.append("avatar", avatar, avatar.name);

      axios
        .post("https://api.chatengine.io/users/", formdata, {
          headers: { "private-key": "af829525-d90f-4169-b0d4-c9525965e699" },
        })
        .then(() => setLoading(false))
        .catch((error) => {
          console.log(error);
        });
    });
  });

  return (
    <div className="chat-page">
      <div className="nav-bar">
        <div onClick={handleLogout} className="logo-tab">
          TrotChat
        </div>
        <div className="logout-tab">LogOut</div>
      </div>
      <ChatEngine
        height="calc(110vh-66px"
        projectID="669aa04f-be14-47ea-a718-4e84ed674962"
        userName={user.email}
        userSecret=""
      />
    </div>
  );
};

export default Chats;
